import React, {Component} from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Nav from '../Navigation'

class Type extends Component {

  constructor(props) {
    super(props)
    this.state = {
      pList: []
    }
    this.getTypeList = this.getTypeList.bind(this);
  }

  componentDidMount(){
    this.getTypeList(10)
}

  getTypeList(type) {
    fetch('https://pokeapi.co/api/v2/type/'+type+'/', {
      headers: {
        "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "*"
      }
  })
    .then(response => {return response.json()})
    .then(poke => {
      poke.pokemon.forEach((element) => {
        let url = element.pokemon.url
        let num = url.substring(34, url.lastIndexOf("/"))
        let imgUrl = "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/"+num+".png"

        this.setState({
          name: element.name,
          img: imgUrl,
          typeName: poke.name
        })
        console.log(element.pokemon.name)
      })
    })
  .catch(error => console.log('Pokemon not found!', error))
  }

  render(){
  return (
    <React.Fragment>
      <CssBaseline />
      <Nav/>
      <Container maxWidth="xl" disableGutters="true">
        <Typography component="div" style={{ backgroundColor: '#cc0000', height: '100vh' }}>
        PokeList
        </Typography>
      </Container>
    </React.Fragment>
  );
}
}
export default Type;
