import React, {Component} from 'react';
import './App.css';
import TextField from '@material-ui/core/TextField';


class App extends Component {
  constructor() {
    super()
    this.state = {
      pokeList:[]
    }
    this.getChamp = this.getChamp.bind(this);
  }

  componentDidMount(){
    this.getChamp()
  }

  getChamp() {
    fetch('https://pokeapi.co/api/v2/type/10/')
    .then(response => response.json())
    .then(poke => {
      this.setState({
        name: poke.pokemon[0].name
      })

    })
    .then(poke => { poke.pokemon.map(poke => ({
      name: `${poke.pokemon.name}`
    }))
  })
  .catch(error => console.log('Champion not found!', error))
}

  render (){
    return(
      <div className="App">
        <TextField disabled label="Name" value={`${this.state.name}`}/>
    </div>)
  };
}

export default App;
