import React, { Component } from "react";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import Nav from "../nav/Navigation";
import Grid from "@material-ui/core/Grid";
import Card from "../Card";
import Modal from "../Modal";

class PokeList extends Component {
  //Create an empty array to store the pokemon in
  constructor(props) {
    super(props);
    this.state = {
      pList: [],
      anchorEl: null,
      setAnchorEl: null
    };
    this.getList = this.getList.bind(this);
  }

  handleClick = event => {
    event.preventDefault();
    this.setState({
      setAnchorEl: event.currentTarget
    });

    handleClose = () => {
      this.setState({
        setAnchorEl: null
      });
    };
    // grab clicked pokemon from pList
    // call moves that are under the Pokemon
    // display moves in modal or menu
    let b = event.target.id;
    console.log(event.target.id);
    console.log(this.state.pList);
    let clicked = this.state.pList.filter(x => x.id === parseInt(b));
    for (var i = 0; i < 4; i++) {
      console.log(clicked[0].moves[i].move.name);
    }
  };

  componentDidMount() {
    //Add each pokemon from the random numbers to the list
    for (var i = 1; i < 13; i++) {
      //Generate 12 random numbers
      let rand = Math.floor(Math.random() * 151);
      this.getList(rand);
    }
  }

  getList(num) {
    fetch("https://pokeapi.co/api/v2/pokemon/" + num + "/", {
      headers: {
        "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "*"
      }
    })
      .then(response => {
        return response.json();
      })
      .then(poke => {
        let pList = [...this.state.pList];
        //add the id, name, and img to the list as an object
        pList.push({
          id: poke.id,
          name: poke.name,
          img: poke.sprites.front_default,
          moves: poke.moves
        });
        this.setState({ pList: pList });
      })
      .catch(error => console.log("Pokemon not found!", error));
  }

  // getList(num) {
  //   fetch("https://pokeapi.co/api/v2/pokemon/" + num + "/", {
  //     headers: {
  //       "Content-Type": "application/json",
  //       "Access-Control-Allow-Origin": "*"
  //     }
  //   })
  //     .then(response => {
  //       return response.json();
  //     })
  //     .then(poke => {
  //       let pList = [...this.state.pList];
  //       //add the id, name, and img to the list as an object
  //       pList.push({
  //         id: poke.id,
  //         name: poke.name,
  //         img: poke.sprites.front_default
  //       });
  //       this.setState({ pList: pList });
  //     })
  //     .catch(error => console.log("Pokemon not found!", error));
  // }

  render() {
    return (
      <React.Fragment>
        <CssBaseline />
        <Nav />
        <Container
          maxWidth="xl"
          disableGutters={true}
          style={{ marginTop: "25px" }}
        >
          <Typography component="div">
            <Grid container>
              {/*Loop through the list and display each object*/}
              {this.state.pList.map((poke, index) => (
                <Grid key={poke.id} item xs={6} md={3}>
                  <Card
                    name={poke.name}
                    img={poke.img}
                    id={poke.id}
                    handleClick={this.handleClick}
                  />
                </Grid>
              ))}
            </Grid>
          </Typography>
        </Container>
      </React.Fragment>
    );
  }
}
export default PokeList;
