import React, {Component} from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Nav from '../Navigation'

class PokeList extends Component {

  constructor() {
    super()
    this.state = {
      pList: [],
      name: '',
      img: ''
    }
    this.getChamp = this.getChamp.bind(this);
  }

  componentDidMount(){
    for (let i = 1; i < 13; i++) {
    this.getChamp(i)
  }
}

  getChamp(type) {
    fetch('https://pokeapi.co/api/v2/pokemon/'+type+'/', {
      headers: {
        "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "*"
      }
  })
    .then(response => {return response.json()})
    .then(poke => {
      let pList = [...this.state.pList]

      pList.push([poke.name, poke.sprites.front_default])

      this.setState({
        name: poke.name,
        img: poke.sprites.front_default,
        pList: pList
      })
    })
  .catch(error => console.log('Pokemon not found!', error))
  }

  render(){
  return (
    <React.Fragment>
      <CssBaseline />
      <Nav/>
      <Container maxWidth="xl" disableGutters="true">
        <Typography component="div" style={{ backgroundColor: '#cc0000', height: '100vh' }}>
        PokeList
        </Typography>
      </Container>
    </React.Fragment>
  );
}
}
export default PokeList;
