import React, { Component } from "react";
import Input from "../TypeSearch";
import TypeList from "../TypeList";
import Nav from "../nav/Navigation";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";

class Type extends Component {
  constructor() {
    super();
    this.state = {
      type: "",
      typeName: "",
      isHidden: true
    };
  }
  //When type is selected call the api
  handleChange = event => {
    event.preventDefault();
    this.setState({
      isHidden: false,
      type: event.target.value
    });
  };

  render() {
    return (
      <React.Fragment>
        <CssBaseline />
        <Nav />
        <Grid container>
          <Grid item md={3} xs={12} alignItems="center">
            <Input
              value={this.state.typeName}
              handleChange={this.handleChange}
            />
          </Grid>
          <Grid item md={9} xs={12}></Grid>
          {/*If the state is currently hidden then load the TypeList component*/}
          {!this.state.isHidden && <TypeList type={this.state.type} />}
        </Grid>
      </React.Fragment>
    );
  }
}

export default Type;
